krpano.action:
if(isTrue,
	set(hotspot[calc(get(caller.name) +'thumb')].url, get(scene[get(caller.linkedscene)].thumburl));
	set(hotspot[calc(get(caller.name) +'thumb')].ath, get(caller.ath));
	set(hotspot[calc(get(caller.name) +'thumb')].atv, get(caller.atv));
	set(hotspot[calc(get(caller.name) +'thumb')].visible, true);
	set(skin_settings.autotour, false);
	autorotate.stop();
	,
	set(hotspot[calc(get(caller.name))].visible, false);
	set(skin_settings.autotour, true);
	if(layer[icon_rotate_off].visible==false,
		autorotate.start();
	)
);

for(set(h,0), h LT hotspot.count, inc(h),
  if(hotspot[get(h)].is_show == true,
    copy(hn,   hotspot[get(h)].name);
    copy(hath, hotspot[get(hn)].ath );
    copy(hatv, hotspot[get(hn)].atv );
    spheretoscreen(hath, hatv, sx, sy);
    if(sx != null AND sy != null AND sx GE 0 AND sx LE get(stagewidth),
      if(sy GE 0 AND sy LE get(stageheight),

         /* ---- FOCUS THEO PIXEL QUANH TÂM ---- */
        calc(cx, get(stagewidth)  * 0.5);
        calc(cy, get(stageheight) * 0.5);
        calc(dxpx, abs(sx - cx));
        calc(dypx, abs(sy - cy));

        /* bán kính vùng chú ý (px) – có thể override per-hotspot bằng attr focus_radius */
        set(focus_radius, 600);
        if(hotspot[get(hn)].focus_radius, copy(focus_radius, hotspot[get(hn)].focus_radius); );

        if(dxpx LE focus_radius AND dypx LE focus_radius,
          set(hotspot[get(hn)].visible,true);
          tween(hotspot[get(hn)].scale,1,1,easeOutExpo);
          tween(hotspot[calc(get(hn)+'line')].height,150,1,easeOutExpo);
        ,
          tween(hotspot[get(hn)].scale,0,1,easeOutExpo);
          tween(hotspot[calc(get(hn)+'line')].height,0,1,easeOutExpo);
        );
      );
    );,
  );
);
